mapper.update:
UPDATE t_customer set name = #{name},tel = #{tel},weixin = #{weixin},address = #{address},email = #{email},userid = #{userid},
pinyin = #{pinyin},companyid = #{companyid},companyname = #{companyname},
level =#{level}
where id = #{id}

mapper.select:
SELECT * FROM t_customer


        userid =#{userid}



order by id desc
